package net.gfu.seminar.jaxrs.helloworld;import javax.ws.rs.GET;import javax.ws.rs.Path;import javax.ws.rs.PathParam;import javax.ws.rs.Produces;import javax.ws.rs.QueryParam;import javax.ws.rs.core.MediaType;/** * RESTful controller using Jersey (JAX-RS) API. * @author tf * */@Path("/helloworld")public class HelloWorld {	/**	 * HTTP GET	 * http://localhost:48080/helloWorldSpringREST/jaxrs/helloworld	 * @return a message as text/plain	 */	@GET @Produces(MediaType.TEXT_PLAIN)	public String sayPlainTextHello() {		return "Hello, world!";	}		/**	 * HTTP GET	 * http://localhost:48080/helloWorldSpringREST/jaxrs/helloworld	 * Client accept: text/xml	 * @return a message as text/xml	 */	@GET @Produces(MediaType.TEXT_XML)	public String sayXMLHello() {		return "<?xml version=\"1.0\"?>" + "<hello> Hello, world!" + "</hello>";	}		/**	 * HTTP GET	 * http://localhost:48080/helloWorldSpringREST/jaxrs/helloworld/to?name=Hans	 * @return the given query param 'name' as text/plain	 */	@GET @Produces(MediaType.TEXT_PLAIN) @Path("to")	public String sayPlainTextHelloTo(@QueryParam("name") String name) {		return "Hello, "+name+"!";	}		/**	 * HTTP GET	 * http://localhost:48080/helloWorldSpringREST/jaxrs/helloworld/person/Anna	 * @return the given name as text/plain	 */	@GET @Produces(MediaType.TEXT_PLAIN) @Path("person/{name}")	public String sayPlainTextHelloToName(@PathParam("name") String name) {		return "Hello, "+name+"!";	}}